var CustomData = bizSdk.CustomData;
async function sendFacebookEvent(data) {
  console.log("[Facebook CAPI] Function called with data:", data);
  const pixelId = process.env.FACEBOOK_PIXEL_ID;
  const accessToken = process.env.FACEBOOK_ACCESS_TOKEN;
  console.log("[Facebook CAPI] Credentials check:", {
    pixelId: pixelId ? "Present" : "MISSING",
    accessToken: accessToken ? "Present" : "MISSING"
  });
  if (!pixelId || !accessToken) {
    console.warn("[Facebook CAPI] Missing FACEBOOK_PIXEL_ID or FACEBOOK_ACCESS_TOKEN");
    return false;
  }
  try {
    bizSdk.FacebookAdsApi.init(accessToken);
    const userData = new UserData().setClientIpAddress(data.ipAddress).setClientUserAgent(data.userAgent);
    if (data.fbp) {
      userData.setFbp(data.fbp);
    }
    if (data.fbc) {
      userData.setFbc(data.fbc);
    }
    if (data.externalId) {
      userData.setExternalId(data.externalId);
      console.log("[Facebook CAPI] External ID added:", data.externalId);
    }
    let customData;
    if (data.customData) {
      customData = new CustomData();
      if (data.customData.content_name) customData.setContentName(data.customData.content_name);
      if (data.customData.content_type) customData.setContentType(data.customData.content_type);
      if (data.customData.currency) customData.setCurrency(data.customData.currency);
      if (data.customData.value) customData.setValue(data.customData.value);
      if (data.customData.num_items) customData.setNumItems(data.customData.num_items);
    }
    const serverEvent = new ServerEvent().setEventName(data.eventName).setEventTime(Math.floor(Date.now() / 1e3)).setEventSourceUrl(data.eventSourceUrl).setUserData(userData).setActionSource("website");
    if (customData) {
      serverEvent.setCustomData(customData);
    }
    const eventRequest = new EventRequest(accessToken, pixelId).setEvents([serverEvent]);
    if (data.testEventCode) {
      eventRequest.setTestEventCode(data.testEventCode);
      console.log("[Facebook CAPI] Using test event code:", data.testEventCode);
    }
    const response = await eventRequest.execute();
    console.log(`[Facebook CAPI] Event sent: ${data.eventName}`, {
      events_received: response.events_received,
      fbtrace_id: response.fbtrace_id
    });
    return true;
  } catch (error) {
    console.error("[Facebook CAPI] Error sending event:", error);
    return false;
  }
}

// server/_core/ipUtils.ts
function isPrivateIP(ip) {
  if (!ip) return true;
  const cleanIP = ip.replace(/^::ffff:/, "");
  const privateRanges = [
    /^127\./,
    // 127.0.0.0/8 (localhost)
    /^10\./,
    // 10.0.0.0/8 (private)
    /^172\.(1[6-9]|2\d|3[01])\./,
    // 172.16.0.0/12 (private)
    /^192\.168\./,
    // 192.168.0.0/16 (private)
    /^169\.254\./,
    // 169.254.0.0/16 (link-local)
    /^0\.0\.0\.0$/,
    // Invalid
    /^::1$/,
    // IPv6 localhost
    /^fe80:/,
    // IPv6 link-local
    /^fc00:/,
    // IPv6 private
    /^fd00:/
    // IPv6 private
  ];
  return privateRanges.some((range) => range.test(cleanIP));
}
function extractClientIP(headers, socketIP) {
  const candidates = [
    headers["cf-connecting-ip"],
    // Cloudflare
    headers["x-real-ip"],
    // Nginx
    headers["x-forwarded-for"],
    // Standard proxy header
    headers["x-client-ip"],
    // Alternative
    socketIP
    // Direct connection
  ];
  for (const candidate of candidates) {
    if (!candidate) continue;
    const ipString = Array.isArray(candidate) ? candidate[0] : candidate;
    if (!ipString) continue;
    const ip = ipString.split(",")[0].trim();
    if (!isPrivateIP(ip)) {
      return ip;
    }
  }
  return "8.8.8.8";
}
function getSafeIPForCAPI(headers, socketIP) {
  const ip = extractClientIP(headers, socketIP);
  console.log("[IP Utils] Extracted IP:", {
    raw: {
      "cf-connecting-ip": headers["cf-connecting-ip"],
      "x-real-ip": headers["x-real-ip"],
      "x-forwarded-for": headers["x-forwarded-for"],
      "socket": socketIP
    },
    final: ip,
    isPrivate: isPrivateIP(ip)
  });
  return ip;
}

