// server/_core/vite.ts
async function setupVite(app, server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true
  };
  const vite = await createViteServer({
    ...vite_config_default,
    configFile: false,
    server: serverOptions,
    appType: "custom"
  });
  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;
    try {
      const clientTemplate = path2.resolve(
        import.meta.dirname,
        "../..",
        "client",
        "index.html"
      );
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e);
      next(e);
    }
  });
}
function serveStatic(app) {
  const distPath = process.env.NODE_ENV === "development" ? path2.resolve(import.meta.dirname, "../..", "dist", "public") : path2.resolve(import.meta.dirname, "public");
  if (!fs.existsSync(distPath)) {
    console.error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`
    );
  }
  app.use(express.static(distPath));
  app.use("*", (_req, res) => {
    res.sendFile(path2.resolve(distPath, "index.html"));
  });
}

// server/api/facebook-capi.ts
async function handleFacebookCAPI(req, res) {
  try {
    console.log("[Facebook CAPI REST] Request received:", {
      method: req.method,
      body: req.body,
      headers: {
        "user-agent": req.headers["user-agent"],
        "x-forwarded-for": req.headers["x-forwarded-for"]
      }
    });
    if (req.method !== "POST") {
      return res.status(405).json({
        success: false,
        error: "Method not allowed. Use POST."
      });
    }
    const { eventName, eventSourceUrl, fbp, fbc, externalId, testEventCode, customData } = req.body;
    if (!eventName || !eventSourceUrl) {
      return res.status(400).json({
        success: false,
        error: "Missing required fields: eventName, eventSourceUrl"
      });
    }
    const validEvents = ["PageView", "ViewContent", "InitiateCheckout", "Purchase"];
    if (!validEvents.includes(eventName)) {
      return res.status(400).json({
        success: false,
        error: `Invalid eventName. Must be one of: ${validEvents.join(", ")}`
      });
    }
    const userAgent = req.headers["user-agent"] || "Mozilla/5.0 (compatible)";
    const ipAddress = getSafeIPForCAPI(req.headers, req.socket.remoteAddress);
    console.log("[Facebook CAPI REST] Extracted data:", {
      userAgent,
      ipAddress,
      eventName,
      fbp,
      fbc,
      externalId
    });
    const success = await sendFacebookEvent({
      eventName,
      eventSourceUrl,
      userAgent,
      ipAddress,
      fbp,
      fbc,
      externalId,
      testEventCode,
      customData
    });
    console.log("[Facebook CAPI REST] Result:", { success });
    return res.status(200).json({ success });
  } catch (error) {
    console.error("[Facebook CAPI REST] Error:", error);
    return res.status(500).json({
      success: false,
      error: "Internal server error"
    });
  }
}

// server/_core/index.ts
function isPortAvailable(port) {
  return new Promise((resolve) => {
    const server = net.createServer();
    server.listen(port, () => {
      server.close(() => resolve(true));
    });
    server.on("error", () => resolve(false));
  });
}
async function findAvailablePort(startPort = 3e3) {
  for (let port = startPort; port < startPort + 20; port++) {
    if (await isPortAvailable(port)) {
      return port;
    }
  }
  throw new Error(`No available port found starting from ${startPort}`);
}
async function startServer() {
  const app = express2();
  const server = createServer(app);
  app.use(express2.json({ limit: "50mb" }));
  app.use(express2.urlencoded({ limit: "50mb", extended: true }));
  registerOAuthRoutes(app);
  app.post("/api/facebook-capi", handleFacebookCAPI);
  app.use(
    "/api/trpc",
    createExpressMiddleware({
      router: appRouter,
      createContext
    })
  );
  if (process.env.NODE_ENV === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }
  const preferredPort = parseInt(process.env.PORT || "3000");
  const port = await findAvailablePort(preferredPort);
  if (port !== preferredPort) {
    console.log(`Port ${preferredPort} is busy, using port ${port} instead`);
  }
  server.listen(port, () => {
    console.log(`Server running on http://localhost:${port}/`);
  });
}
startServer().catch(console.error);
